import {
    REQUESTING_ALL_POKEMON_DATABASE, 
    FAIL_REQUEST_ALL_POKEMON_DATABASE,
    SUCCESS_REQUEST_ALL_POKEMON_DATABASE} from '../constants/constants';
import axios from 'axios';


export const getPokeDataBase = ()=>(dispatch)=>{


    dispatch({type: REQUESTING_ALL_POKEMON_DATABASE})
    axios.get(`http://localhost:3001/pokemons/dataBaseGet`)
    .then(
        (resp)=>{ 
            dispatch({type:  SUCCESS_REQUEST_ALL_POKEMON_DATABASE, payload: resp.data}) 
        },
        (err)=>{ 
            dispatch({type:  FAIL_REQUEST_ALL_POKEMON_DATABASE , payload: err}) 
        }
    );
};
