import {
    REQUESTING_POKEMON_TYPE, 
    REQUESTING_TYPE_ID_FAIL,
    REQUESTING_TYPE_ID_SUCCESS } from '../constants/constants';
import axios from 'axios';


export const searchTypeId = (payload)=>(dispatch)=>{
    const id= payload;

      dispatch({type: REQUESTING_POKEMON_TYPE })
    if(id){
        dispatch({type:REQUESTING_TYPE_ID_SUCCESS, payload:id})
    }else{
        dispatch({type:REQUESTING_TYPE_ID_FAIL, payload:'ERROR DE SISTEMA'})
    }


};
