import {
    CREATE_POKEMON, 
    FAIL_CREATE_POKEMON,
    SUCCESS_CREATE_POKEMON } from '../constants/constants';

const initialState = {
    createPokemons:{
        results: false,
        errorPoke: '',
        loading: false,
    }
}

const reducer = (state= initialState, action)=>{
    switch(action.type){
        case CREATE_POKEMON:
            return {
                ...state.createPokemons, loading: true
            }
        case FAIL_CREATE_POKEMON:
            return {
                ...state.createPokemons, errorPoke: action.payload, loading:false
            }    
        case  SUCCESS_CREATE_POKEMON:
            return {
                ...state.createPokemons, results: action.payload, loading:false
            }

        return state 
    }
}

export default reducer;
