import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Card from '../card/Card';
import NavBar from '../navbar/NavBar';
import Search from '../search/Search';
import Select from '../select/Select';
import {pokeTypeGet} from '../../redux/action/select';
import './Home.css';
import { getPokeApi } from '../../redux/action/getPokeApi';
import { pagination, quickSort } from './order';

const Home =  ()=>{

    // Visualización de pokemon
    const dispatch = useDispatch()
    const state = useSelector((state) => state);
    const { pokemon } = useSelector((state) => state);
    const  { select, order } =  useSelector((state) => state);

    const [cardPoke, setCardPoke]=React.useState(false);


    React.useEffect( ()=>{
        console.log(state)
        let poke= localStorage.getItem('poke')

        if(pokemon){
            // let resul = quickSort(pokemon.results)
            // console.log('Es el resultado: ', resul)
            setCardPoke(pokemon)
        };
        if(order && poke ){
            if(order.type === 'upward' ){
                let resul = JSON.parse(poke);
                let p = quickSort(resul.results);
                console.log(p)
                setCardPoke({results: p, loading: false, errorPoke: ''})
            }else{
                let resul = JSON.parse(poke);
                let p = quickSort(resul.results,'desending');
                console.log(p)
                setCardPoke({results: p, loading: false, errorPoke: ''})
            }
        }
        dispatch(pokeTypeGet())   
    },[pokemon, order])

    if(cardPoke && !cardPoke.loading && cardPoke.length !== 0){
        let resul = JSON.stringify(cardPoke)
        localStorage.setItem('poke', resul)
    }


    // DATOS PARA LOS SELECT
    const dataTypes= {title: false, types:[]};
    if(select !== undefined && select.results.length !== 0){
        dataTypes.title='Select type';
        dataTypes.types= select.results;


    }

    // PAGINACION 
    const [pag, setPag]=React.useState({
        index:0,
        data:[]
    })
    const handleClick= (e)=>{
        const id = e.target.id
        let poke= localStorage.getItem('poke')
        let resul = JSON.parse(poke)
        let paginacion= pagination(resul.results, 3)
        setPag({
            ...pag, data: paginacion
        })
        let count = 0
        if(id === 'next'){
            if(pag.index < pag.data.length){
                setPag({
                    ...pag, index: pag.index + 1
                })
                setCardPoke({results: pag.data[pag.index], loading:false, errorPoke: ''})
            }
        }else{
            if(pag.index > 0){
                setPag({
                    ...pag, index: pag.index - 1
                })
                setCardPoke({results: pag.data[pag.index], loading:false, errorPoke: ''})
            }
        }
    }



    console.log(select)
    const dataSelectOrder={
        title: 'Order by',
        types:[
            {
                id:21,
                name:'upward',
                img: 'https://png.pngtree.com/png-vector/20190411/ourlarge/pngtree-vector-up-arrow-icon-png-image_925729.jpg'
            },
            {
                id:22,
                name:'falling',
                img:'https://i.pinimg.com/originals/0a/bf/ab/0abfab8799e4234e418b0cad4ed1d864.png'
            },
            {
                id:23,
                name:'weak',
                img:'https://media.istockphoto.com/vectors/weak-male-arms-with-flexed-biceps-muscles-vector-id1255435696?b=1&k=6&m=1255435696&s=170667a&w=0&h=HCc_HBCKP_gz1UDDtRgW1-euAWtxIOSb-UPRKO-L4Ig='
            },
            {
                id:24,
                name:'strong',
                img: 'https://thumbs.dreamstime.com/b/brazo-fuerte-del-m%C3%BAsculo-93007112.jpg'
            },
            {
                id:25,
                name:'database',
                img:'data:image/png;base64,'
            },
            {
                id:26,
                name:'Api',
                img: 'https://accessusergroups.org/access-latino/wp-content/uploads/sites/3/2018/03/ApiRest.png'
            },

        ]
    }

    return(
        <div className='HMcontainer'>
            <div className='HMnavBar'>
                <div className='HMcolor'>
                    <NavBar/>
                </div>

            </div>

            <div className='HMsearch'>
                <Search/>
                <div className='HMcard'>   
                {
                    cardPoke === false ?('Esperando'):
                    cardPoke.loading ? ('LOADING'):
                    cardPoke.errorPoke ? ('POKEMON NOT FOUND'):
                    (cardPoke.results.map((e)=>{return(<Card pokemon={e}/>)}))

                }
                </div>
                <div className='HMselect'>
                   {
                       dataTypes.title === 'Select type' ?(<Select datos={dataTypes} from={{name:'home'}}/>):('')
                   }
                </div>
                <div className='HMselectOrder'>
                     <Select datos={dataSelectOrder} from={{name:'home'}}/>
                </div>

            </div>

        </div>
    )
}
export default Home;
