import {
    SUCCESS_REQUEST_POKEMON_BY_NAME, 
    FAIL_REQUEST_POKEMON_BY_NAME,
    REQUESTING_POKEMON_BY_NAME } from '../constants/constants';
import axios from 'axios';


export const searchByName = (payload)=>(dispatch)=>{
    const name= payload;

    dispatch({type:  REQUESTING_POKEMON_BY_NAME })
    axios.get(`http://localhost:3001/pokemons/searchByNameApi?name=${name}`)

    .then(
        (resp)=>{ 
            dispatch({type:  SUCCESS_REQUEST_POKEMON_BY_NAME , payload: resp.data}) 
        },
        (err)=>{ 
            dispatch({type:  FAIL_REQUEST_POKEMON_BY_NAME , payload: err}) 
        }
    );
};
