import React from 'react';
import './Search.css';
import { useDispatch, useSelector } from "react-redux";
import { searchByName } from '../../redux/action/searchByName';

const Search = ()=>{


    const dispatch= useDispatch()

    const [name, setName] = React.useState(
        {
            data: ''
        }
    )
    const handleInput = (e)=>{
        const value = e.target.value;
        setName(
            {
                ...name, data: value
            }
        )
    }


    const handleButton = ()=>{}
    const handelEnter = (k)=>{
        const key = k.key;
        if(key === 'Enter'){
            dispatch(searchByName(name.data))
        }
    }

    return(
            <div className='container'>

                <div className='containerSearchBar'>


                        <div className='SearchBar'>
                        <input className= 'input' type="text" placeholder='Search Here...' onChange={handleInput} onKeyPress={handelEnter}/>
                        <label className='label' onClick={handleButton}>
                            <i className='fas fa-search'></i>
                        </label> 
                        </div>                    


                </div> 

        </div>   
    )
}

export default Search;
