import {
    ORDER  } from '../constants/constants';


const initialState = {
    order: {
        type: ''
    }
}

const reducer = (state= initialState, action)=>{
    switch(action.type){
        case ORDER:
            return {
                ...state.order, type: action.payload
            }
        return state;

    }
}

export default reducer;
