import {
    CREATE_POKEMON, 
    FAIL_CREATE_POKEMON,
    SUCCESS_CREATE_POKEMON } from '../constants/constants';
import axios from 'axios';


export const postPokemon = (payload)=>(dispatch)=>{
    const id= payload;

    dispatch({type: CREATE_POKEMON})
    axios.post(`http://localhost:3001/pokemons`, {pokemon:payload,loadingPokemonType: true})
    .then(
        (resp)=>{ 
            dispatch({type:  SUCCESS_CREATE_POKEMON , payload: 'Se creo el pokemon'}) 
        },
        (err)=>{ 
            dispatch({type: FAIL_CREATE_POKEMON , payload: 'No se creo el pokemon'}) 
        }
    );
};
