import React from 'react';
import { useDispatch } from 'react-redux';
import { getPokeApi } from '../../redux/action/getPokeApi';
import { getPokeDataBase } from '../../redux/action/getPokeDatabase';
import { order } from '../../redux/action/order';
import { searchTypeId } from '../../redux/action/searchTypeId';
import { pokeTypeGet } from '../../redux/action/select';
import './Select.css';

const Select = ({datos, from})=>{

    const dispatch= useDispatch()
    const [active, setActive]= React.useState(false);

    const handleSelect= ()=>{
        if(!active){
            setActive(true)
        }else{
            setActive(false)
        }
    };


    // SELECCIONAR UN POKEMON

    const [selected, setSelected]= React.useState({
        type:false
    })
    const handleOption = (P)=>{
        setSelected({
            ...selected, type: P
        })
        let typeID= 0;
        if(from && from.name === 'dashboard'){
             typeID= P.id;
             localStorage.setItem('id1', typeID)
        };
        if(from && from.name === 'dashboard2'){
            typeID= P.id;
            localStorage.setItem('id2', typeID)
       };
       if(from && from.name === 'home'){
           if(P.name === 'Api'){
             dispatch(getPokeApi())
           }
           if(P.name === 'database'){
            dispatch(getPokeDataBase())
          }
          if(P.name === 'upward'){
              console.log('ESTOY ACAAA')
              dispatch(order('upward'))
          };
          if(P.name === 'falling'){
             dispatch(order('falling'))
        };

       }

    }

    return (
        <div className='SLcontainer'>

                <div className='SLselectBox' onClick={handleSelect}>
                    {
                        active ? (
                            <div className='SLselect active'>
                            <div className='SLcontentSelect'>
                            {
                                !selected.type ? (
                                    <>
                                    <img  className='SLimg' src='data:image/png;base64,' alt=""/>
                                     <div className='SLtext'>
                                            <h1 className='SLtitle'>{datos.title}</h1>
                                            <p className='SLdescription'>lorem ipsum dolor sit</p>
                                        </div>
                                    </>
                                ):(
                                    <>
                                        <img  className='SLimg' src= {selected.type.img} alt=""/>
                                        <div className='SLtext'>
                                            <h1 className='SLtitle'>{selected.type.name}</h1>
                                            <p className='SLdescription'>lorem ipsum dolor sit</p>
                                        </div>
                                    </>
                                )
                                }
                            </div>
                            <i className='fas  fa-angle-down SLicon'></i>
                        </div>
                        ):(
                            <div className='SLselect '>
                            <div className='SLcontentSelect'>
                                {
                                !selected.type ? (
                                    <>
                                          <img  className='SLimg' src='data:image/png;base64,' alt=""/>
                                       <div className='SLtext'>
                                            <h1 className='SLtitle'>{datos.title}</h1>
                                            <p className='SLdescription'>lorem ipsum dolor sit</p>
                                        </div>
                                    </>
                                ):(
                                    <>
                                        <img  className='SLimg' src= {selected.type.img} alt=""/>
                                        <div className='SLtext'>
                                            <h1 className='SLtitle'>{selected.type.name}</h1>
                                            <p className='SLdescription'>lorem ipsum dolor sit</p>
                                        </div>
                                    </>
                                )
                                }
                            </div>
                            <i className='fas  fa-angle-down SLicon'></i>
                        </div>
                        )
                    }
                    {
                        active ? (
                         <div className='SLoption active'>
                             {
                                 datos.types.map(P => {
                                     return (
                                        <div  onClick={()=>{handleOption(P)}}>
                                        <div className='SLcontentOption'>
                                            <img  className='SLimg' src= {P.img} alt=""/>
                                            <div className='SLtext'>
                                            <h1 className='SLtitle'>{P.name}</h1>
                                            <p className='SLdescription'>lorem ipsum dolor sit</p>
                                            </div>
                                        </div>
                                    </div>
                                     )
                                 })
                             }
                        </div>
                        ):(
                            <div className='SLoption'>
                            <div href='#'>
                                <div className='SLcontentOption'>
                                    <img  className='SLimg' src="https://www.flaticon.es/svg/vstatic/svg/197/197573.svg?token=exp=1616818600~hmac=e5b9b5f25fa691bf4bf41b0530d251b2" alt=""/>
                                    <div className='SLtext'>
                                    <h1 className='SLtitle'>Argentina</h1>
                                    <p className='SLdescription'>lorem ipsum dolor sit</p>
                                    </div>
                                </div>
                            </div>

                        </div>
                        )
                    }
                </div>

        </div>
    )
}

export default Select;
