import {
    REQUESTING_ALL_POKEMON_API, 
    FAIL_REQUEST_ALL_POKEMON_API,
    SUCCESS_REQUEST_ALL_POKEMON_API} from '../constants/constants';
import axios from 'axios';


export const getPokeApi = ()=>(dispatch)=>{


    dispatch({type: REQUESTING_ALL_POKEMON_API})
    axios.get(`http://localhost:3001/pokemons/apiGet`)
    .then(
        (resp)=>{ 
            dispatch({type:  SUCCESS_REQUEST_ALL_POKEMON_API, payload: resp.data}) 
        },
        (err)=>{ 
            dispatch({type:  FAIL_REQUEST_ALL_POKEMON_API , payload: err}) 
        }
    );
};
