import  order  from './order';
import  postPokemon  from './postPokemon';
import searchByName from './searchByName';
import select from './select';

const InitialState = {
    order: {
        type:''
    }
}
const combineReducer = (state={}, action)=>{
    return(
        {
            pokemon: searchByName(state.pokemons, action),
            select: select(state.pokemonsType, action),
            postPokemons: postPokemon(state.createPokemons, action),
            order: order(state.order, action)
        }
    )
}
export default combineReducer;
