import {
    REQUESTING_POKEMON_TYPE, 
    FAIL_REQUEST_POKEMON_TYPE,
    SUCCESS_REQUEST_POKEMON_TYPE,
    REQUESTING_TYPE_ID_FAIL,
    REQUESTING_TYPE_ID_SUCCESS  } from '../constants/constants';


const reducer = (state, action)=>{
    switch(action.type){
        case REQUESTING_POKEMON_TYPE:
            return {
                ...state.pokemonsType, loading: true
            }
        case FAIL_REQUEST_POKEMON_TYPE:
            return {
                ...state.pokemonsType, errorPoke: action.payload, loading:false
            }    
        case SUCCESS_REQUEST_POKEMON_TYPE:
            return {
                ...state.pokemonsType, results: action.payload, loading:false
            }

        case REQUESTING_TYPE_ID_SUCCESS:
            return {
                ...state.pokemonsType, typeId: action.payload, loading:false
            }

        case REQUESTING_TYPE_ID_FAIL:
            return {
                ...state.pokemonsType, errorId: action.payload, loading:false
            }

    }
}

export default reducer;
