import axios from 'axios';
import React, { useEffect } from 'react';
import './CardDetails.css';

const CardDetails = ({match})=>{

    const [data, setData]= React.useState(
        false
    )
    React.useEffect(()=>{
        if(match.params.id < 900){
            axios.get(`http://localhost:3001/pokemons/searchByIdApi/${match.params.id}`)
        .then(
            (resp)=>{setData(resp)},
            (err)=>{setData(err)}
        )
        }else{
            axios.get(`http://localhost:3001/pokemons/searchByIdDataBase/${match.params.id}`)
        .then(
            (resp)=>{setData(resp)},
            (err)=>{setData(err)}
        )
        }

    }, [])


    if(match.params.id < 900){


        return(
            <div className='CScontainer'>
                {
                    data === false ? ('LOADING'):(
                        <>
                            <div className='CSscreen1'>
                            <div className='CSheader'>
                                    <h1 className='CStitle'>{data.data.name}</h1>
                            </div>
                            <div className='CSstatistics'>
                                <h1 className='Csname'>Statistics</h1>
                                <div className='CSdiv'>
                                <p>HP:{data.data.stats[0].base_stat} </p>
                                <p>ATTACK:{data.data.stats[1].base_stat} </p>
                                <p>SPEED: {data.data.stats[4].base_stat}</p>
                                <p>DEFENSE: {data.data.stats[2].base_stat}</p>
                                <p>WEIGHT: {data.data.weight} </p>
                                <p>HEIGHT: {data.data.height}  </p>
                                {data.data.types.map(e =>{return <p>TYPE: {e.type.name} </p>})}
                                </div>

                            </div>
                        </div>
                        <div className='CSscreen2'>
                        <div className='CSimg'>
                                <img className='CSimage' src={data.data.sprites.other.dream_world.front_default} alt=""/>
                            </div>
                        </div>
                    </>
                    )

                }
            </div>
        )
    }else{
        return(
            <div className='CScontainer'>
             {
                    data === false ? ('LOADING'):(
                        <>
                            <div className='CSscreen1'>
                            <div className='CSheader'>
                                    <h1 className='CStitle'>{data.data.name}</h1>
                            </div>
                            <div className='CSstatistics'>
                                <h1 className='Csname'>Statistics</h1>
                                <div className='CSdiv'>
                                <p>HP:{data.data.hp} </p>
                                <p>ATTACK:{data.data.attack} </p>
                                <p>SPEED: {data.data.speed}</p>
                                <p>DEFENSE: {data.data.defense}</p>
                                <p>WEIGHT: {data.data.weight} </p>
                                <p>HEIGHT: {data.data.height}  </p>
                                {data.data.types.map(e =>{<p>TYPE: {e.name} </p>})}
                                </div>

                            </div>
                        </div>
                        <div className='CSscreen2'>
                        <div className='CSimg'>
                                <img className='CSimage' src={data.data.sprites.other.dream_world.front_default} alt=""/>
                            </div>
                        </div>
                    </>
                    )

                }
        </div>
        )
    }
}

export default CardDetails;
